package com.sajansthapit.mytodolist.app.category.models;import com.sajansthapit.mytodolist.app.base.model.BaseModel;import com.sajansthapit.mytodolist.app.todouser.models.TodoUser;import jakarta.persistence.*;import lombok.*;import java.util.Objects;@Getter@Setter@Entity@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "todo_user_category")public class TodoUserCategory extends BaseModel {    @Id    private String id;    @ManyToOne(cascade = CascadeType.ALL, fetch = FetchType.LAZY)    @JoinColumn(name = "todo_user_id", referencedColumnName = "id")    private TodoUser todoUser;    @ManyToOne(cascade = CascadeType.ALL, fetch = FetchType.LAZY)    @JoinColumn(name = "category_id", referencedColumnName = "id")    private Category category;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof TodoUserCategory that)) return false;        if (!super.equals(o)) return false;        return Objects.equals(id, that.id) && Objects.equals(todoUser, that.todoUser) && Objects.equals(category, that.category);    }    @Override    public int hashCode() {        return Objects.hash(super.hashCode(), id, todoUser, category);    }}