package com.sajansthapit.mytodolist.app.todouser.controller;import com.sajansthapit.mytodolist.app.dto.BaseResponse;import com.sajansthapit.mytodolist.app.todouser.dto.requestdto.TodoUserDto;import com.sajansthapit.mytodolist.app.todouser.service.ITodoUserService;import jakarta.validation.Valid;import lombok.AllArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@AllArgsConstructor@RequestMapping("/user")public class TodoUserController {    private ITodoUserService todoUserService;    @PostMapping("/register")    public ResponseEntity<BaseResponse> save(@RequestBody @Valid TodoUserDto todoUserDto){        todoUserService.save(todoUserDto);        BaseResponse response = new BaseResponse("User created successfully");        return new ResponseEntity<>(response, HttpStatus.CREATED);    }}