package com.sajansthapit.mytodolist.app.todouser.dto.requestdto;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotEmpty;import jakarta.validation.constraints.Pattern;public record TodoUserDto(        @NotBlank(message = "Full name is mandatory")        @NotEmpty(message = "Full name can't be empty")        String fullName,        @NotBlank(message = "Email is mandatory")        @NotEmpty(message = "Email can't be empty")        @Pattern(regexp = "(\\S.*\\S)(@)(\\S.*\\S)(.\\S[a-z]{2,3})", message = "Invalid email format")        String email,        @NotBlank(message = "Password is mandatory")        @NotEmpty(message = "Password can't be empty")        @Pattern(regexp = "^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[@$!%*?&])[A-Za-z\\d@$!%*?&]{8,}$", message = "Invalid password provided")        String password) {}