package com.sajansthapit.mytodolist.app.todouser.service.impl;import com.sajansthapit.mytodolist.app.todouser.dto.requestdto.TodoUserDto;import com.sajansthapit.mytodolist.app.todouser.models.TodoUser;import com.sajansthapit.mytodolist.app.todouser.service.ITodoUserService;import com.sajansthapit.mytodolist.app.todouser.repository.TodoUserRepository;import lombok.AllArgsConstructor;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.security.crypto.password.PasswordEncoder;import org.springframework.stereotype.Service;import java.util.UUID;@Service@AllArgsConstructorpublic class TodoUserDetailsService implements UserDetailsService, ITodoUserService {    private final TodoUserRepository todoUserRepository;    private final PasswordEncoder passwordEncoder;    @Override    public UserDetails loadUserByUsername(String email) {        return todoUserRepository.findByEmail(email).orElseThrow(() -> new UsernameNotFoundException("User not found"));    }    @Override    public TodoUser save(TodoUserDto todoUserDto) {        String encryptedPassword = passwordEncoder.encode(todoUserDto.password());        TodoUser todoUser = TodoUser.builder()                .email(todoUserDto.email())                .password(encryptedPassword)                .fullName(todoUserDto.fullName())                .build();        todoUser.setId(UUID.randomUUID().toString());        todoUser.setActive(true);        return todoUserRepository.save(todoUser);    }}