package com.sajansthapit.mytodolist.controller;import com.sajansthapit.mytodolist.dto.userdto.UserDto;import com.sajansthapit.mytodolist.models.TodoUser;import com.sajansthapit.mytodolist.service.TodoUserDetailsService;import lombok.AllArgsConstructor;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@AllArgsConstructor@RequestMapping("/user")public class TodoUserController {    private TodoUserDetailsService userDetailsService;    @PostMapping("/register")    public TodoUser save(@RequestBody UserDto userDto){        return userDetailsService.createUser(userDto);    }}