package com.sajansthapit.mytodolist.controller;import com.sajansthapit.mytodolist.dto.BaseResponse;import com.sajansthapit.mytodolist.dto.userdto.UserDto;import com.sajansthapit.mytodolist.service.TodoUserDetailsService;import lombok.AllArgsConstructor;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@AllArgsConstructor@RequestMapping("/user")public class TodoUserController {    private TodoUserDetailsService userDetailsService;    @PostMapping("/register")    public ResponseEntity<BaseResponse> save(@RequestBody UserDto userDto){        userDetailsService.createUser(userDto);        BaseResponse response = new BaseResponse("User created successfully");        return new ResponseEntity<>(response, HttpStatus.CREATED);    }}