package com.sajansthapit.mytodolist.models;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.Objects;@Getter@Setter@Entity@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "category")public class Category extends BaseModel {    @Id    @Column(name = "id")    private String id;    @Column(name = "name", unique = true, nullable = false)    private String name;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof Category category)) return false;        if (!super.equals(o)) return false;        return Objects.equals(id, category.id) && Objects.equals(name, category.name);    }    @Override    public int hashCode() {        return Objects.hash(super.hashCode(), id, name);    }}