package com.sajansthapit.mytodolist.models;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@Entity@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "category")public class Category extends BaseModel {    @Id    @Column(name = "id")    private String id;    @Column(name = "name", unique = true, nullable = false)    private String name;}