package com.sajansthapit.mytodolist.models;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.Objects;@Getter@Setter@Entity@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "status")public class Status extends BaseModel{    @Id    private String id;    @Column(name = "status", unique = true, nullable = false)    private String name;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof Status status)) return false;        if (!super.equals(o)) return false;        return Objects.equals(id, status.id) && Objects.equals(name, status.name);    }    @Override    public int hashCode() {        return Objects.hash(super.hashCode(), id, name);    }}