package com.sajansthapit.mytodolist.models;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@Entity@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "task")public class Task extends BaseModel {    @Id    private String id;    @Column(name = "tittle", nullable = false)    private String title;    @Column(name = "description", columnDefinition = "text", nullable = false)    private String description;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "category_id", referencedColumnName = "id")    private Category category;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "status_id", referencedColumnName = "id")    private Status status;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "priority_id", referencedColumnName = "id")    private Priority priority;}