package com.sajansthapit.mytodolist.models;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.Objects;@Getter@Setter@Entity@Builder@NoArgsConstructor@AllArgsConstructor@Table(name = "task")public class Task extends BaseModel {    @Id    private String id;    @Column(name = "tittle", nullable = false)    private String title;    @Column(name = "description", columnDefinition = "text", nullable = false)    private String description;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "category_id", referencedColumnName = "id")    private Category category;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "status_id", referencedColumnName = "id")    private Status status;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "priority_id", referencedColumnName = "id")    private Priority priority;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (!(o instanceof Task task)) return false;        if (!super.equals(o)) return false;        return Objects.equals(id, task.id) && Objects.equals(title, task.title) && Objects.equals(description, task.description) && Objects.equals(category, task.category) && Objects.equals(status, task.status) && Objects.equals(priority, task.priority);    }    @Override    public int hashCode() {        return Objects.hash(super.hashCode(), id, title, description, category, status, priority);    }}