package com.sajansthapit.mytodolist.service.impl;import com.sajansthapit.mytodolist.dto.userdto.UserDto;import com.sajansthapit.mytodolist.models.TodoUser;import com.sajansthapit.mytodolist.repository.TodoUserRepository;import com.sajansthapit.mytodolist.service.ITodoUserService;import lombok.AllArgsConstructor;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.security.crypto.password.PasswordEncoder;import org.springframework.stereotype.Service;import java.util.UUID;@Service@AllArgsConstructorpublic class TodoUserDetailsService implements UserDetailsService, ITodoUserService {    private final TodoUserRepository todoUserRepository;    private final PasswordEncoder passwordEncoder;    @Override    public UserDetails loadUserByUsername(String email) {        return todoUserRepository.findByEmail(email).orElseThrow(() -> new UsernameNotFoundException("User not found"));    }    @Override    public TodoUser save(UserDto userDto) {        String encryptedPassword = passwordEncoder.encode(userDto.password());        TodoUser todoUser = TodoUser.builder()                .email(userDto.email())                .password(encryptedPassword)                .fullName(userDto.fullName())                .build();        todoUser.setId(UUID.randomUUID().toString());        todoUser.setActive(true);        return todoUserRepository.save(todoUser);    }}